import 'package:get/get.dart';import 'package:our_building/view/screens/auth/register_user_screen.dart';import '../../core/functions/transitions.dart';import '../../view/screens/auth/register_providerService_screen.dart';class ChooseAuthTypeController extends GetxController {  bool isUserSelected = true;  changeSelected() {    isUserSelected = !isUserSelected;    update();  }  goToRegisterUserScreen(context) {    push(context, const RegisterUserScreen());  }  goToRegisterProviderServiceScreen(context) {    push(context, const RegisterProviderServiceScreen());  }}