import 'package:flutter/material.dart';import 'package:our_building/core/class/app_data/app_assets.dart';import 'package:our_building/view/screens/auth/choose_auth_way_screen.dart';import 'package:get/get.dart';import '../../core/functions/transitions.dart';class OnBoardingController extends GetxController {  PageController pageController = PageController(initialPage: 0);  int currentScreen = 0;  int lengthPages = 3;  next(context) {    currentScreen++;    if (currentScreen >= lengthPages) {      pushReplcement(        context,        const ChooseAuthWayScreen(),      );    } else {      update();    }  }  skip(context) {    currentScreen = 3;    next(context);  }  List<OnBoardingModel> onBoardingData = [    OnBoardingModel(      imgName: AppAssets.onBoardingImg1,      desc: 'ابحث عن أفضل المقاولين ',      nextText: 'التالي',    ),    OnBoardingModel(      imgName: AppAssets.onBoardingImg2,      desc: 'هل تملك عقارا تريد بيعه \nهنا يكمن الامان ',      nextText: 'التالي',    ),    OnBoardingModel(      imgName: AppAssets.onBoardingImg3,      desc: 'استثمر وقتك معنا وابحث عن مقدم خدمة',      nextText: 'ابدأ الآن',    ),  ];}class OnBoardingModel {  String imgName;  String desc;  String nextText;  OnBoardingModel(      {required this.imgName, required this.desc, required this.nextText});}