import 'dart:ffi';import 'package:get/get.dart';String serviseDesc =    'اكتشف مجموعتنا المتميزة من مواد البناء \n لتحويل مشاريعك إلى واقع ملموس \n جودة عالية , أداء متفوق , وقيمة مضمونة\n ابدأ اليوم وابنِ غدك بثقة';List<String> servicesTitle = [  'تجار مواد بناء',  'مقاولون',  'مكاتب هندسية',  'تنفيذ وتركيب ورش',  'معدات وعدد',  'عمال فنية',  'النقل',  'مطورون عقاريون',];class ChooseServiceHomeController extends GetxController {  List<ServiceDataModel> serviceData = servicesTitle.map((e) {    return ServiceDataModel(      title: e,      dec: serviseDesc,      imgPath: '',    );  }).toList();}class ServiceDataModel {  final String title;  final String dec;  final String imgPath;  ServiceDataModel(      {required this.title, required this.dec, required this.imgPath});}