import 'package:flutter/material.dart';import 'package:our_building/controller/auth/register_user_controller.dart';import 'package:our_building/core/class/app_data/app_assets.dart';import 'package:our_building/core/class/app_data/app_color.dart';import 'package:our_building/core/constant/constant_user_data.dart';import 'package:our_building/core/functions/transitions.dart';import 'package:our_building/view/screens/home_screens/main_home_screen.dart';import '../../../core/functions/spaces.dart';import 'package:our_building/core/class/size_config/size_config.dart';import 'package:our_building/view/widgets/auth/custom_title_and_textfield.dart';import 'package:our_building/view/widgets/custom_public_widgets/custom_button.dart';import 'package:our_building/view/widgets/custom_public_widgets/custom_text.dart';import 'package:get/get.dart';import 'package:flutter_svg/flutter_svg.dart';import '../../widgets/custom_public_widgets/custom_logo_app.dart';class RegisterUserScreen extends StatelessWidget {  const RegisterUserScreen({super.key});  @override  Widget build(BuildContext context) {    Get.put(RegisterUserController());    return Directionality(      textDirection: TextDirection.rtl,      child: Scaffold(        backgroundColor: Colors.white,        body: SafeArea(          child: SingleChildScrollView(            physics: const BouncingScrollPhysics(),            child: Padding(              padding: EdgeInsets.symmetric(                horizontal: SizeConfig.scaleWidth(20),              ),              child: GetBuilder<RegisterUserController>(builder: (logic) {                return Column(                  children: [                    spaceH(30),                    Row(                      crossAxisAlignment: CrossAxisAlignment.center,                      // mainAxisAlignment: MainAxisAlignment.spaceEvenly,                      children: [                        IconButton(                          onPressed: () {                            print('Hi');                            Navigator.pop(context);                          },                          // color: AppColor.mainAppColor,                          icon: const Icon(Icons.arrow_back_ios),                        ),                        spaceW(20),                        CustomText('إنشاء حساب كمستخدم', 14, Colors.black),                      ],                    ),                    // const CustomLogoApp(                    //   height: 350,                    //   imgPath: AppAssets.signUp,                    //   isSvg: true,                    // ),                    const CustomImageApp(path: AppAssets.signUp, height: 350,isSvg: true),                    spaceH(0),                    ListView.builder(                      physics: const NeverScrollableScrollPhysics(),                      shrinkWrap: true,                      itemCount: logic.itemsTitleAndTextField.length,                      itemBuilder: (BuildContext context, int index) {                        var item = logic.itemsTitleAndTextField[index];                        return CustomTitleAndTextField(                          item.title,                          item.hint,                          item.controller,                          item.textInputType,                          item.isPass,                          acceptedTheme: false,                        );                      },                    ),                    spaceH(30),                    Row(                      children: [                        Checkbox(                          value: logic.checkValue,                          onChanged: (value) {                            logic.changeCheckValue(value);                          },                        ),                        CustomText(                          'أوافق على الشروط والسياسات',                          12,                          Colors.black87,                        ),                      ],                    ),                    spaceH(15),                    CustomButton(                      Center(                        child: CustomText(                          'إنشاء حساب',                          14,                          Colors.white,                          fontWeight: FontWeight.w600,                        ),                      ),                      () {                        StaticUserData.isUser = true;                        pushAndRemoveAll(context, const MainHomeScreen());                      },                      double.infinity,                      50,                      AppColor.mainAppColor,                    ),                    spaceH(20),                  ],                );              }),            ),          ),        ),      ),    );  }}