import 'package:flutter/material.dart';import 'package:our_building/controller/auth/verfication_code_controller.dart';import 'package:our_building/core/class/app_data/app_color.dart';import '../../../core/functions/spaces.dart';import '../../../core/functions/transitions.dart';import 'package:our_building/core/class/size_config/size_config.dart';import 'package:our_building/view/screens/auth/reset_password_screen.dart';import 'package:our_building/view/widgets/custom_public_widgets/custom_button.dart';import 'package:our_building/view/widgets/custom_public_widgets/custom_text.dart';import '../../widgets/auth/custom_title_and_textfield.dart';import 'package:get/get.dart';import '../../widgets/auth/register_user_widgets/custom_verficationCode_textField.dart';import '../../widgets/custom_public_widgets/custom_logo_app.dart';class VerificationCodeScreen extends StatelessWidget {  const VerificationCodeScreen({super.key});  @override  Widget build(BuildContext context) {    Get.put(VerficationCodeController());    return Scaffold(      backgroundColor: Colors.white,      body: SafeArea(        child: SingleChildScrollView(          physics: const BouncingScrollPhysics(),          child: Directionality(            textDirection: TextDirection.rtl,            child: Padding(              padding: EdgeInsets.symmetric(                horizontal: SizeConfig.scaleWidth(20),              ),              child: GetBuilder<VerficationCodeController>(builder: (logic) {                return Column(                  children: [                    spaceH(50),                    const CustomLogoApp(height: 200),                    spaceH(40),                    Align(                        alignment: Alignment.centerRight,                        child:                            CustomText('تحقق من البريد', 18, Colors.black87)),                    spaceH(30),                    CustomText(                        'أدخل الكود المرسل إلى البريد :', 14, Colors.grey),                    spaceH(10),                    CustomText('example@gmail.com', 14, Colors.grey),                    spaceH(20),                    const CustomVerficationCodeTextField(),                    spaceH(20),                    logic.isLoading == false                        ? CustomButton(                            Center(                              child: CustomText(                                'تحقق',                                14,                                Colors.white,                                fontWeight: FontWeight.w600,                              ),                            ),                            () async => await logic.verfication(context),                            double.infinity,                            50,                            AppColor.mainAppColor,                          )                        : SizedBox(                            height: SizeConfig.scaleHeight(50),                            width: double.infinity,                            child: Center(                              child: CircularProgressIndicator(                                color: AppColor.mainAppColor,                              ),                            ),                          ),                    spaceH(15),                    CustomText('لم أستلم الرمز', 12, Colors.black87),                    TextButton(                      onPressed: () {},                      child: CustomText('إعادة الإرسال', 10, Colors.red),                    ),                  ],                );              }),            ),          ),        ),      ),    );  }}