import 'package:flutter/material.dart';import 'package:our_building/controller/home_controllers/home_controller.dart';import 'package:our_building/controller/home_controllers/main_home_controller.dart';import 'package:our_building/core/class/app_data/app_color.dart';import 'package:our_building/core/class/extentions/size-config-extentions.dart';import 'package:our_building/core/functions/transitions.dart';import 'package:our_building/view/screens/home_screens/home/home_services_screen/service_details_screen.dart';import 'package:our_building/view/widgets/custom_public_widgets/custom_text.dart';import 'package:get/get.dart';import 'package:our_building/view/widgets/main_app_screens/home_screen_widgets/custom_item_provider_service.dart';import '../../../../../core/class/size_config/size_config.dart';import '../../../../../core/functions/spaces.dart';import '../../../../widgets/main_app_screens/home_screen_widgets/search_field.dart';class ChooseServicesHomeScreen extends StatelessWidget {  const ChooseServicesHomeScreen({super.key});  @override  Widget build(BuildContext context) {    final ChooseServiceHomeController controller =        Get.put(ChooseServiceHomeController());    return Padding(      padding: EdgeInsets.symmetric(        vertical: SizeConfig.scaleHeight(15),        horizontal: SizeConfig.scaleWidth(5),      ),      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          CustomSearchField(            (value) => null,            TextEditingController(),            'ابحث عن الخدمة التي تريدها',          ),          spaceH(20),          CustomText('اختر نوع الخدمة', 15,              Theme.of(context).textTheme.titleLarge!.color!),          Divider(            height: SizeConfig.scaleHeight(20),            thickness: 0.5,            color: Colors.grey,            endIndent: 20,            indent: 20,          ),          Expanded(            // height: SizeConfig.screenHeight * 0.62,            child: ListView.separated(              physics: const BouncingScrollPhysics(),              shrinkWrap: true,              itemBuilder: (context, index) {                return CustomItemServiceHomeScreen(                    title: controller.serviceData[index].title,                    desc: controller.serviceData[index].dec);              },              separatorBuilder: (context, index) {                return Divider(                  height: 10.h,                  color: Colors.grey,                  thickness: SizeConfig.scaleHeight(0.8),                  indent: SizeConfig.scaleWidth(50),                  endIndent: SizeConfig.scaleWidth(50),                );              },              itemCount: controller.serviceData.length,            ),          ),        ],      ),    );  }}class CustomItemServiceHomeScreen extends StatelessWidget {  final String title;  final String desc;  const CustomItemServiceHomeScreen(      {super.key, required this.title, required this.desc});  @override  Widget build(BuildContext context) {    final ChooseServiceHomeController controller = Get.find();    return GestureDetector(      onTap: () {        push(            context,            ServiceDetailsScreen(              title: title,            ));      },      child: Container(        margin: EdgeInsets.symmetric(          vertical: SizeConfig.scaleHeight(10),        ),        padding: EdgeInsets.symmetric(          vertical: SizeConfig.scaleHeight(15),          horizontal: SizeConfig.scaleWidth(10),        ),        decoration: BoxDecoration(          color: Theme.of(context).cardColor,          borderRadius: BorderRadius.circular(15),        ),        child: Row(          children: [            Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                CustomText(                  title,                  15,                  AppColor.mainAppColor,                  fontWeight: FontWeight.w600,                ),                spaceH(5),                SizedBox(                  width: SizeConfig.screenWidth - SizeConfig.scaleWidth(120),                  child: CustomText(                    desc,                    11,                    Theme.of(context).textTheme.titleLarge!.color!,                    textAlign: TextAlign.start,                  ),                ),              ],            ),            SizedBox(              height: SizeConfig.scaleHeight(90),              width: SizeConfig.scaleWidth(90),              child: const CustomContainerClipperContainImage(),            )          ],        ),      ),    );  }}