import 'package:flutter/material.dart';import 'package:our_building/controller/home_controllers/main_home_controller.dart';import 'package:flutter_svg/flutter_svg.dart';// ignore: depend_on_referenced_packagesimport 'package:get/get.dart';import 'package:our_building/core/class/app_data/app_color.dart';import 'package:our_building/core/class/size_config/size_config.dart';import 'package:our_building/core/functions/spaces.dart';import 'package:our_building/view/widgets/custom_public_widgets/custom_logo_app.dart';import 'package:our_building/view/widgets/custom_public_widgets/custom_text.dart';import '../../../core/class/app_data/app_assets.dart';class MainHomeScreen extends StatelessWidget {  const MainHomeScreen({super.key});  @override  Widget build(BuildContext context) {    final controller = Get.put(MainHomeController());    return WillPopScope(      onWillPop: () async {        return await controller.willPopScop(context);      },      child: GetBuilder<MainHomeController>(builder: (logic) {        return Directionality(          textDirection: TextDirection.rtl,          child: Scaffold(            backgroundColor: Theme.of(context).scaffoldBackgroundColor,            bottomNavigationBar: bottomNavBar(context),            appBar: AppBar(              leading: InkWell(                onTap: () {                  logic.showSettings(context);                },                child: Image.asset(                  AppAssets.settingsIcon,                  scale: 1.3,                ),              ),              actions: [                SvgPicture.asset(                  AppAssets.logoApp,                  height: 40,                ),                spaceW(10),              ], elevation: 0,              centerTitle: true,              backgroundColor: Theme.of(context).primaryColor,              title: CustomText(                logic.appBarTitles[logic.currentIndex].toString(),                14,                Colors.white,              ),            ),            body: logic.screens[logic.currentIndex],          ),        );      }),    );  }}Widget bottomNavBar(context) {  MainHomeController logic = Get.find();  return Theme(    data: ThemeData(      canvasColor:Theme.of(context).primaryColor,      bottomNavigationBarTheme: const BottomNavigationBarThemeData(        elevation: 10,        backgroundColor: Colors.black,      ),    ),    // sets the inactive color of the `BottomNavigationBar`    child: SizedBox(        height: SizeConfig.scaleHeight(55),        child: BottomNavigationBar(          enableFeedback: true,      selectedLabelStyle: TextStyle(        color: Colors.white,        fontSize: SizeConfig.scaleTextFont(12),        height: SizeConfig.scaleHeight(1.5),        fontFamily: 'Tajawal',      ),      elevation: 15,      backgroundColor: AppColor.mainAppColor,      // تعيين لون الخلفية إلى الأسود هنا      items: logic.bottomNavItem,      currentIndex: logic.currentIndex,      showSelectedLabels: true,      // selectedItemColor: Colors.black,      // تعيين لون العناصر المحددة إلى الأسود هنا      onTap: (value) {        logic.changeIndex(value);      },    )),  );}