// ignore_for_file: must_be_immutableimport 'package:flutter/material.dart';import '../../../core/class/size_config/size_config.dart';class CustomButton extends StatelessWidget {  Widget child;  Function() function;  double width;  double height;  Color color;  double radius;  CustomButton(this.child, this.function, this.width, this.height, this.color,      {super.key, this.radius = 10});  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap: function,      child: Container(        height: SizeConfig.scaleHeight(height + SizeConfig.scaleHeight(5)),        width: SizeConfig.scaleWidth(width),        decoration: BoxDecoration(          color: color,          borderRadius: BorderRadius.circular(radius),        ),        child: child,      ),    );  }}