// ignore_for_file: must_be_immutableimport 'package:flutter/material.dart';import 'package:our_building/core/class/size_config/size_config.dart';class CustomText extends StatelessWidget {  String txt;  double size;  FontWeight fontWeight;  Color color;  TextAlign textAlign;  CustomText(this.txt, this.size, this.color,      {this.fontWeight = FontWeight.w500, this.textAlign = TextAlign.center});  @override  Widget build(BuildContext context) {    return Text(      txt,      style: TextStyle(        fontSize: SizeConfig.scaleTextFont(size),        fontWeight: fontWeight,        color: color,        fontFamily: 'Tajawal',      ),      textAlign: textAlign,    );  }}